    function increaseStat(
        uint256 cloneId,
        IStats.Stat stat,
        uint256 amount
    ) external returns (bool);

    function decreaseStat(
        uint256 cloneId,
        IStats.Stat stat,
        uint256 amount
    ) external returns (bool);
}
